CREATE TABLE IF NOT EXISTS OrderLinesProduct (Id INTEGER PRIMARY KEY AUTOINCREMENT, ProductId INTEGER NOT NULL, CreatedDate LONG NOT NULL, Quantity INTEGER NOT NULL, OrderId LONG NOT NULL);
CREATE TABLE IF NOT EXISTS OrderLinesManual (Id INTEGER PRIMARY KEY AUTOINCREMENT, Name TEXT, Price INTEGER NOT NULL, CreatedDate LONG NOT NULL, OrderId LONG NOT NULL);
CREATE TABLE IF NOT EXISTS Orders (Id INTEGER PRIMARY KEY AUTOINCREMENT, Reference TEXT, CreatedDate LONG NOT NULL);